'use strict';
var fs = require('fs');
var assert = require('assert');
var gutil = require('gulp-util');
var cssmin = require('./index');

it('should minify style', function (cb) {
	var stream = cssmin();
	stream.on('data', function (file) {
		assert(file.contents.length < fs.statSync(__dirname + '/sample/type.style').size);
		cb();
	});

	stream.write(new gutil.File({
		path: __dirname + '/sample/type.style',
		contents: fs.readFileSync(__dirname + '/sample/type.min.style')
	}));
});
